// src/components/RuleForm.js
import React, { useState } from 'react';
import axios from 'axios';


const RuleForm = () => {
  const [ruleString, setRuleString] = useState('');
  const [response, setResponse] = useState('');


  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const result = await axios.post('http://localhost:5001/api/rules', { rule: ruleString });
      setResponse(result.data);
    } catch (error) {
      console.error('Error creating rule:', error);
      setResponse('Error creating rule');
    }
  };


  return (
    <div>
      <h2>Create a Rule</h2>
      <form onSubmit={handleSubmit}>
        <textarea
          value={ruleString}
          onChange={(e) => setRuleString(e.target.value)}
          placeholder="Enter your rule here..."
          rows="4"
          cols="50"
          required
        />
        <br />
        <button type="submit">Submit Rule</button>
      </form>
      {response && <p>Response: {response}</p>}
    </div>
  );
};


export default RuleForm;
