// src/components/RuleList.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

function RuleList() {
    const [rules, setRules] = useState([]);

    // Fetch rules when the component loads
    useEffect(() => {
        const fetchRules = async () => {
            try {
                const response = await axios.get('http://localhost:5001/api/rules');
                setRules(response.data);
            } catch (error) {
                console.error('Error fetching rules:', error);
            }
        };
        fetchRules();
    }, []);

    // Function to delete a rule
    const deleteRule = async (id) => {
        try {
            await axios.delete(`http://localhost:5001/api/rules/${id}`);
            setRules(rules.filter(rule => rule._id !== id)); // Remove rule from local state
        } catch (error) {
            console.error('Error deleting rule:', error);
        }
    };

    return (
        <div className="rule-list">
            <h2>Existing Rules</h2>
            <ul>
                {rules.map((rule) => (
                    <li key={rule._id}>
                        {rule.rule}
                        <button onClick={() => deleteRule(rule._id)}>Delete</button>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default RuleList;
